// @brief：耗时统计函数
func timeCost(start time.Time) {
	tc := time.Since(start)
	fmt.Printf("time cost = %v\n", tc)
}

func longestDupSubstring(s string) string {
	crc := crc32.New(crc32.MakeTable(crc32.Koopman))
	//crc := crc32.New(crc32.MakeTable(0))
	hash := func(str string) uint32 {
		crc.Reset()
		crc.Write([]byte(str))
		return crc.Sum32()
	}

	check := func(ln int) (string, bool) {
		cache := make(map[uint32][2]int)
		for i := 0; i <= len(s)-ln; i++ {
			sub := s[i : i+ln]
			h := hash(sub)
			if v, ok := cache[h]; ok && s[v[0]:v[1]] == sub {
				return sub, true
			}
			cache[h] = [...]int{i, i + ln}
		}
		return "", false
	}

	left, right := 0, len(s)-1
	var ans string
	for left <= right {
		mid := (left + right) >> 1
		if t, ok := check(mid); ok {
			left = mid + 1 // 找到了，那就以mid +1 去试试，看看有没有更长一些的
			ans = t
		} else {
			right = mid - 1 // 如果没找到，那就找一个更短一点的字符串
		}
	}
	return ans
}

func longestDupSubstringErr(s string) string {
	defer timeCost(time.Now())

	var maxLen int
	var l, r int

	bs := []byte(s)

	for i := 0; i < len(s); i++ {
		for j := i; j < len(s); j++ {
			if j+1-i > maxLen {
				sub := bs[i : j+1]
				if d := Count(bs[i:], sub); d > 1 && (j+1-i) > maxLen {
					maxLen = len(sub)
					l = i
					r = j
				}
			}
		}
	}
	if maxLen == 0 {
		return ""
	}
	return s[l : r+1]
}

func Count(s, sep []byte) int {
	// special case
	if len(sep) == 0 {
		return utf8.RuneCount(s) + 1
	}
	if len(sep) == 1 {
		var ans int
		for i := 0; i < len(s); i++ {
			if s[i] == sep[0] {
				ans++
				if ans == 2 {
					break
				}
			}
		}
		return ans
	}
	n := 0
	for {
		i := bytes.Index(s, sep)
		if i == -1 {
			return n
		}
		n++
		if n == 2 {
			return n
		}
		s = s[i+1:]
	}
}
