func longestValidParentheses(s string) int {
	if len(s) < 2 {
		return 0
	}
	// ()
	// ))

	//我们定义 dp[i] 表示以下标 i 字符结尾的最长有效括号的长度
	dp := make([]int, len(s))
	//dp[0] = 0

	max := 0

	for i := 1; i < len(s); i++ {
		if s[i] == ')' {
			if s[i-1] == '(' {
				dp[i] = 2
				if i-2 >= 0 {
					dp[i] += dp[i-2]
				}
			} else {
				if i-dp[i-1] > 0 && s[i-dp[i-1]-1] == '(' {
					dp[i] = dp[i-1] + 2
					if j := i - dp[i-1] - 2; j >= 0 {
						dp[i] += dp[i-dp[i-1]-2] //前面还有一节单独成对的

					}
				}
			}
		}
		if dp[i] > max {
			max = dp[i]
		}
	}
	return max
}

func longestValidParentheses2(s string) int {
	n := len(s)
	dp := make([][]bool, n)
	for i := range dp {
		dp[i] = make([]bool, n)
	}

	max := -1

	for i := len(s) - 1; i >= 0; i-- {
		for j := i; j < len(s); j++ {
			if s[i] == '(' && s[j] == ')' {
				if j-i <= 1 || dp[i+1][j-1] {
					dp[i][j] = true
				} else {
					for k := 1; k < (j - i); k++ {
						if dp[i][i+k] && dp[i+k+1][j] {
							dp[i][j] = true
						}
					}
				}
			}

			if dp[i][j] && j-i > max {
				max = j - i
			}
		}
	}
	if max != -1 {
		return max + 1
	}
	return 0
}
