func isValid2(skeys map[byte]int, k int) bool {
	for _, val := range skeys {
		if val < k {
			return false
		}
	}
	return true
}

func longestSubstring(s string, k int) int {

	ls := 0
	for i := 0; i < len(s); i++ {
		skeys := make(map[byte]int, 27)
		for j := i; j < len(s); j++ {
			skeys[s[j]]++

			if isValid2(skeys, k) && j-i+1 > ls {
				ls = j - i + 1
			}
		}
	}

	return ls
}

func longestSubstring2(s string, k int) (ans int) {
	n := len(s)
	if n == 0 || k > n {
		return 0
	}
	if k < 2 {
		return n
	}

	return countLongestSubstring2(s, k, 0, n-1) // 这里传递的是 n 不是 n-1，左闭右开

}

// p1 可以取到 p2 取不到
func countLongestSubstring2(s string, k, p1, p2 int) int {

	if p2-p1+1 < k {
		return 0
	}

	times := [26]int{}
	for i := p1; i <= p2; i++ {
		times[s[i]-'a']++
	}

	for p2-p1+1 >= k && times[s[p1]-'a'] < k {
		p1++
	}

	for p2-p1+1 >= k && times[s[p2]-'a'] < k {
		p2--
	}

	if p2-p1+1 < k {
		return 0
	}
	for i := p1; i <= p2; i++ {
		if times[s[i]-'a'] < k {
			return Max(countLongestSubstring2(s, k, p1, i-1), countLongestSubstring2(s, k, i+1, p2))
		}
	}

	return p2 - p1 + 1
}

func longestSubstring3(s string, k int) (ans int) {
	times := map[string]int{}

	for i := 0; i < len(s); i++ {
		times[string(s[i])]++
	}

	// strings.Split()
	seps := []string{}
	for kk, vv := range times {
		if vv < k {
			seps = append(seps, kk)
		}
	}

	arr := []string{}
	for _, sep := range seps {
		arr = append(arr, strings.Split(s, sep)...)
	}

	fmt.Println(arr)

	return 0

}

